set is_delete=1,
    update_by=#{delUser},
    update_time=#{delDate}
where id = #{modelId}

mapper.select:
select

from sys_role_menu
where is_delete=0 and id = #{id}

select

from sys_role_menu
where is_delete = 0

    ${sql}

order by create_time desc,order_by desc
limit #{pageNum},#{pageSize}
