mapper.update:
update sys_user_role
set is_delete=1,
    update_by=#{delUser},
    update_time=#{delDate}
where id = #{modelId}

mapper.select:
select
from sys_user_role ur left join sys_user u on ur.user_id = u.id left join sys_role r on ur.role_id = r.id where ur.is_delete=0 and u.is_delete=0 and r.is_delete=0
${sql}
order by ur.create_time desc, ur.order_by desc limit #{pageNum},#{pageSize}
