mapper.update:
update sys_role_menu
set is_delete=1,
    last_user_id=#{delUser},
    last_user_name=#{delUserName},
    last_time=#{delDate}
where sys_id = #{modelId}

mapper.select:
select

from sys_role_menu
where is_delete=0 and sys_id = #{id}

select

from sys_role_menu
where is_delete = 0

    ${sql}

order by create_time desc,order_by desc
limit #{page},#{pageSize}

select count(*)
from sys_role_menu
where is_delete = 0
and name = #{name}

    and sys_id != #{id}
