mapper.update:
update sys_user_role
set is_delete=1,
    update_user_id=#{delUser},
    update_user_name=#{delUserName},
    update_time=#{delDate}
where sys_id = #{modelId}

mapper.select:
select

from sys_user_role
where is_delete=0 and sys_id = #{id}

select

from sys_user_role
where is_delete = 0

    ${sql}

order by create_time desc, order_by desc
limit #{page},#{pageSize}
